package modchu.playerformclear;import java.util.Map;import net.minecraft.client.entity.EntityPlayerSP;import net.minecraft.client.renderer.entity.RenderManager;import cpw.mods.fml.client.registry.RenderingRegistry;import cpw.mods.fml.common.Mod;import cpw.mods.fml.common.Mod.EventHandler;import cpw.mods.fml.common.event.FMLInitializationEvent;@Mod(modid="PlayerFormClear", name="PlayerFormClear", version="2")public class PlayerFormClear {	public String getVersion() {		return "1.6.4-2";	}	@EventHandler	public void init(FMLInitializationEvent event) {		RenderingRegistry.registerEntityRenderingHandler(EntityPlayerSP.class, new RenderPlayerFormClear());	}}